// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 <0.9.0;


contract RefugeeRegistry {
    struct Refugee {
        string name;
        string ipfsHash; // stores base64 encoded face descriptor
    }

    mapping(address => Refugee) public refugees;
    address[] public registeredUsers;

    function registerRefugee(string memory _name, string memory _ipfsHash) public {
        require(bytes(_name).length > 0, "Name cannot be empty");
        require(bytes(_ipfsHash).length > 0, "IPFS hash cannot be empty");

        if (bytes(refugees[msg.sender].ipfsHash).length == 0) {
            registeredUsers.push(msg.sender); // only add if first time
        }

        refugees[msg.sender] = Refugee(_name, _ipfsHash);
    }

    function getMyData() public view returns (string memory, string memory) {
        Refugee memory r = refugees[msg.sender];
        return (r.name, r.ipfsHash);
    }

    function getAllUsers() public view returns (address[] memory) {
        return registeredUsers;
    }

    function getRefugeeByAddress(address _addr) public view returns (string memory, string memory) {
        Refugee memory r = refugees[_addr];
        return (r.name, r.ipfsHash);
    }
}
